import React, { useState, useEffect  } from "react";
import { Grid } from "@material-ui/core";
import Appbar from "../appbar/appbar.js";
import { styled } from "@mui/material/styles";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import Paper from "@material-ui/core/Paper";
import Box from "@material-ui/core/Box";
import Avatar from "@material-ui/core/Avatar";
import { useSpring, animated } from "react-spring";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Link from "@material-ui/core/Link";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { connect } from "react-redux";
import { setAreas } from "../../redux/actions.js";
import { useHistory } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { sizing } from '@mui/system';


import "react-tabs/style/react-tabs.css";
import { FaCentercode } from "react-icons/fa";

const buildPath = require("../../redux/buildPath");

function AccessCodePage(props) {
  const classes = useStyles();
  const location = useLocation();
  let history = useHistory();
  const [registrationFormStatus, setRegistartionFormStatus] = useState(false);


const loginBtnProps = useSpring({
  borderBottom: registrationFormStatus 
    ? "solid 0px transparent"
    : "solid 2px #27AE60",  //Animate bottom border of login button
    color:registrationFormStatus
    ? "#808080"
    :"#27AE60", 
    fontSize: "15px",
});
const registerBtnProps = useSpring({
  borderBottom: registrationFormStatus
    ? "solid 2px #27AE60"
    : "solid 0px transparent", //Animate bottom border of register button
    color:registrationFormStatus
    ? "#27AE60"
    :"#808080",
    fontSize: "15px",
});

function registerClicked() {
  setRoleShown(false);
  setRegistartionFormStatus(true);
}
function loginClicked() {
  setRoleShown(true);
  setRegistartionFormStatus(false);
}

const [roleShown, setRoleShown] = useState(true);
